import React from "react";
import { Button, Card } from "react-bootstrap";
import { Link } from "react-router-dom";
// import { uuid } from "uuidv4";

const UserCard = ({ data }) => {
  function formatDate(t, a, s) {
    function format(m) {
      let f = new Intl.DateTimeFormat("en", m);
      return f.format(t);
    }
    return a.map(format).join(s);
  }

  let format = [{ day: "numeric" }, { month: "short" }, { year: "numeric" }];

  return (
    <div className="d-flex justify-content-center pt-4">
      <Card bg={"light"} text={"dark"}>
        <Card.Header>
          <h2>{data.name.first + " " + data.name.last}</h2>
        </Card.Header>
        <Card.Body>
          <Card.Title>Card Title </Card.Title>
          <Card.Text>
            <div>
              <div>
                <img src={data.picture.large} alt={"Profile pic"} />
              </div>
              <p>{data.email}</p>
              <p>
                {data.location.street.number +
                  " " +
                  data.location.street.name +
                  " " +
                  data.location.city +
                  ", " +
                  data.location.state +
                  " " +
                  data.location.postcode +
                  " " +
                  data.location.country}
              </p>
              <p>{data.phone}</p>
              <p>{formatDate(new Date(data.dob.date), format, "/")}</p>
            </div>
          </Card.Text>
        </Card.Body>
        <Card.Footer>Footer</Card.Footer>
      </Card>
    </div>
  );
};

export default UserCard;
