import React, { Component, useState, useEffect } from "react";
import { Route, Switch } from "react-router-dom";
import "./App.css";
import Main from "./pages/Main";
import Details from "./pages/Details";

const App = () => {
  const [usersList, setUsersList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [offset, setOffset] = useState(0);

  //on mount function to call API from backend with page number as
  //parameter then order the users in the table and set state variables
  useEffect(() => {
    const getUsers = async () => {
      setLoading(true);
      var temp = [];
      var seed = "abc";

      await fetch("/api/getList/" + seed)
        .then((res) => res.json())
        .then((list) => {
          temp = list;
        });

      //set a new seed to create new, consistent data
      seed = "def";

      await fetch("/api/getList/" + seed)
        .then((res) => res.json())
        .then((list) => {
          temp.push.apply(temp, list); //combine both API call lists
          temp.sort((a, b) => (a.name.last > b.name.last ? 1 : -1));
        });
      setUsersList(temp);
      setLoading(false);
    };
    getUsers();
  }, []);

  const App = () => (
    <div>
      <Switch>
        <Route
          exact
          path="/"
          component={() => (
            <Main
              usersList={usersList}
              loading={loading}
              offset={offset}
              setOffset={setOffset}
            />
          )}
        />
        <Route path="/details" component={Details} />
      </Switch>
    </div>
  );
  return (
    <Switch>
      <App />
    </Switch>
  );
};

export default App;
