import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Button, Spinner } from "react-bootstrap";
import DataTable from "../components/DataTable";

class Main extends Component {
  // Initialize the state
  constructor(props) {
    super(props);
    this.state = {
      usersList: [],
      loading: false,
    };
    this.logInfo = this.logInfo.bind(this);
  }

  // Fetch the list on first mount
  componentDidMount() {
    this.getList();
  }

  // Retrieves the list of items from the Express app
  getList = async () => {
    this.setState({ loading: true });
    console.log("List: ");
    await fetch("/api/getList")
      .then((res) => res.json())
      .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
      .then((list) => this.setState({ usersList: list, loading: false }));
  };

  logInfo() {
    console.log(this.state.usersList);
    console.log("gender: " + this.state.usersList[0].gender);
    var tempArr = [1, 2, 3, "yes", "no"];
    console.log(tempArr);
  }

  render() {
    return (
      <div className="App">
        <h1>List of Users</h1>
        <Button size="lg" variant="success" onClick={this.logInfo}>
          LOG
        </Button>

        {this.state.loading ? (
          <DataTable data={this.state.usersList} />
        ) : (
          <Spinner animation="border" variant="primary" />
        )}

        {/* Check to see if any items are found*/}
        {/* {this.state.loading ? (
          <Spinner animation="border" variant="primary" />
        ) : (
          <React.Fragment>
            {this.state.usersList.map((item, index) => (
              <div>
                <p key={item.id.value}>
                  {item.name.first + " " + item.name.last}
                </p>
                <p key={item.id.value}>{item.email}</p>
                <p key={item.id.value}>
                  {item.location.city + ", " + item.location.country}
                </p>
                <Link
                  to={{
                    pathname: "./Details",
                    state: { user: item },
                  }}
                >
                  <button variant="raised">View Details</button>
                </Link>
                <p>--------------------------------------------------</p>
              </div>
            ))}
          </React.Fragment>
        )} */}

        <Link to={"./Details"}>
          <button variant="raised">View Details</button>
        </Link>
      </div>
    );
  }
}

export default Main;
