import React, { Component } from "react";
import { Link } from "react-router-dom";

class Main extends Component {
  // Initialize the state
  constructor(props) {
    super(props);
    this.state = {
      usersList: [],
      loading: false,
    };
    this.logInfo = this.logInfo.bind(this);
  }

  // Fetch the list on first mount
  componentDidMount() {
    this.getList();
  }

  // Retrieves the list of items from the Express app
  getList = async () => {
    this.setState({ loading: true });
    console.log("List: ");
    await fetch("/api/getList")
      .then((res) => res.json())
      .then((list) => this.setState({ usersList: list, loading: false }));
  };

  logInfo() {
    console.log(this.state.usersList);
    console.log("gender: " + this.state.usersList[0].gender);
    var tempArr = [1, 2, 3, "yes", "no"];
    console.log(tempArr);
  }

  render() {
    return (
      <div className="App">
        <h1>List of Items</h1>
        <button onClick={this.logInfo}>LOG</button>
        {/* Check to see if any items are found*/}
        {this.state.loading ? (
          <p>LOADING</p>
        ) : (
          <ul>
            {this.state.usersList.map((item, index) => (
              <p key={item.id}>{item.gender}</p>
            ))}
          </ul>
        )}

        <Link to={"./Details"}>
          <button variant="raised">View Details</button>
        </Link>
      </div>
    );
  }
}

export default Main;
