import React, { Component } from "react";
import { Button, Spinner } from "react-bootstrap";
import DataTable from "../components/DataTable";
import Pages from "../components/Pages";

class Main extends Component {
  // Initialize the state
  constructor(props) {
    super(props);
    this.state = {
      usersList: [],
      loading: false,
      page: 1,
    };
  }

  // Fetch the list on first mount
  componentDidMount() {
    this.getList();
  }

  // Retrieves the list of items from the Node (Express) backend
  getList = async () => {
    this.setState({ loading: true });
    await fetch("/api/getList/" + this.state.page)
      .then((res) => res.json())
      .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
      .then((list) => this.setState({ usersList: list, loading: false }));
  };

  render() {
    return (
      <div className="App">
        <div style={{ margin: 50 }}>
          <h1>Clozd Assignment</h1>
          <h3>Mason Rich</h3>
        </div>
        {/* If loading show spinner, else show data table */}
        {this.state.loading ? (
          <Spinner animation="border" variant="primary" />
        ) : (
          <DataTable data={this.state.usersList} />
        )}
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Pages />
        </div>
      </div>
    );
  }
}

export default Main;
