import React, { useEffect, useState } from "react";
import { Spinner } from "react-bootstrap";
import DataTable from "../components/DataTable";
import DataTable2 from "../components/DataTable2";

import Pages from "../components/Pages";

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      usersList: [],
      loading: false,
      page: 1,
      seed: "abc",
    };
  }

  //component mount and user retrieval
  componentDidMount() {
    this.getUsers();
    console.log("mounted");
  }

  //function to call API from backend with page number as parameter
  //then order the users in the table and set state variables
  getUsers = async () => {
    this.setState({ loading: true });
    var temp = [];
    await fetch("/api/getList/" + this.state.seed)
      .then((res) => res.json())
      // .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
      .then((list) => {
        // setUsersList(list);
        temp = list;
        this.setState({ seed: "def" });
      });
    await fetch("/api/getList/" + this.state.seed)
      .then((res) => res.json())
      // .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
      .then((list) => {
        // setUsersList(list);
        // temp = list;
        temp.push.apply(temp, list);
      });
    this.setState({ usersList: temp, loading: false });
  };

  render() {
    return (
      <div className="App">
        <div style={{ margin: 50 }}>
          <h1>Clozd Assignment</h1>
          <h3>Mason Rich</h3>
        </div>
        {/* If loading show spinner, else show data table */}
        {this.state.loading ? (
          <Spinner animation="border" variant="primary" />
        ) : (
          // <DataTable data={usersList} />
          <DataTable2
            data={this.state.usersList}
            page={this.state.page}
            setPage={this.state.setPage}
          />
        )}
        {/* <Pages setPage={setPage} page={page} /> */}
      </div>
    );
  }
}

export default Main;
