import React, { Component } from "react";
import { Route, Switch } from "react-router-dom";
import "./App.css";
import Main from "./pages/Main";
import Details from "./pages/Details";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      usersList: [],
      loading: false,
      page: 1,
      seed: "abc",
    };
  }

  //component mount and user retrieval
  componentDidMount() {
    this.getUsers();
  }

  //function to call API from backend with page number as parameter
  //then order the users in the table and set state variables
  getUsers = async () => {
    this.setState({ loading: true });
    var temp = [];
    await fetch("/api/getList/" + this.state.seed)
      .then((res) => res.json())
      .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
      .then((list) => {
        temp = list;
        this.setState({ seed: "def" });
      });
    await fetch("/api/getList/" + this.state.seed)
      .then((res) => res.json())
      .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
      .then((list) => {
        //combine both API call lists
        temp.push.apply(temp, list);
      });
    this.setState({ usersList: temp, loading: false });
  };

  render() {
    const App = () => (
      <div>
        <Switch>
          <Route
            exact
            path="/"
            component={() => (
              <Main
                usersList={this.state.usersList}
                loading={this.state.loading}
              />
            )}
          />
          <Route path="/details" component={Details} />
        </Switch>
      </div>
    );
    return (
      <Switch>
        <App />
      </Switch>
    );
  }
}

export default App;
