import React, { Component, useState, useEffect } from "react";
import { Route, Switch } from "react-router-dom";
import { Spinner } from "react-bootstrap";
import "./App.css";
import Main from "./pages/Main";
import Details from "./pages/Details";

const App = () => {
  const [usersList, setUsersList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [columns, setColumns] = useState({});
  const [tableData, setTableData] = useState([]);

  //on mount function to call API from backend with page number as
  //parameter then order the users in the table and set state variables
  useEffect(() => {
    const getUsers = async () => {
      setLoading(true);
      var temp = [];
      var seed = "abc";

      //get the first 3500 users
      //since we need 7000 users and the API limits to 5000 at a time,
      //we will need to fetch this API twice
      await fetch("/api/getList/" + seed)
        .then((res) => res.json())
        .then((list) => {
          temp = list;
        });

      //set a new seed to create new, consistent data
      seed = "def";

      //get the next 3500 users
      await fetch("/api/getList/" + seed)
        .then((res) => res.json())
        .then((list) => {
          temp.push.apply(temp, list); //combine both API call lists
          temp.sort((a, b) => (a.name.last > b.name.last ? 1 : -1));
        });

      setUsersList(temp);
      // setLoading(false);
      //////////////////////////////////////////////////////////////////
      const cols = [
        { title: "#", field: "number" },
        { title: "Last Name", field: "lastName" },
        { title: "First Name", field: "firstName" },
        { title: "Email", field: "email" },
        { title: "City", field: "city" },
        { title: "Country", field: "country" },
        { title: "Button", field: "button" },
      ];

      //convert data for table usage
      var xxx = [];
      data.forEach((user, i) => {
        var obj = {};
        obj["number"] = i + 1;
        obj["lastName"] = user.name.last;
        obj["firstName"] = user.name.first;
        obj["email"] = user.email;
        obj["city"] = user.location.city;
        obj["country"] = user.location.country;
        obj["button"] = (
          <Link
            to={{
              pathname: "./Details",
              state: { user: user },
            }}
            className="text-info"
          >
            Visit Profile
          </Link>
        );

        xxx.push(obj);
      });

      setTableData(xxx);
      setColumns(cols);

      setLoading(false);
    };
    getUsers();
  }, []);

  const App = () => (
    <div className="App">
      {loading ? (
        <div>
          <h4 className={"mt-5"}>Loading Main Page</h4>
          <Spinner animation="border" variant="primary" />
        </div>
      ) : (
        <Switch>
          <Route
            exact
            path="/"
            component={() => <Main usersList={usersList} />}
          />
          <Route path="/details" component={Details} />
        </Switch>
      )}
    </div>
  );
  return (
    <Switch>
      <App />
    </Switch>
  );
};

export default App;
