import React, { Component } from "react";
import { Link } from "react-router-dom";

class Details extends Component {
  // Initialize the state
  constructor(props) {
    super(props);
    this.state = {
      usersList: [],
      loading: false,
    };
    this.logInfo = this.logInfo.bind(this);
  }

  // Fetch the list on first mount
  componentDidMount() {
    this.getList();
  }

  // Retrieves the list of items from the Express app
  getList = async () => {
    this.setState({ loading: true });
    console.log("List: ");
    await fetch("/api/getList")
      .then((res) => res.json())
      .then((list) => this.setState({ usersList: list, loading: false }));
  };

  logInfo() {
    console.log(this.state.usersList);
  }

  render() {
    return (
      <div className="App">
        <h1>List of Items</h1>
        <button onClick={this.logInfo}>LOG</button>
        {/* Check to see if any items are found*/}
        {this.state.loading ? (
          <p>LOADING</p>
        ) : (
          <p>{this.state.usersList.gender}</p>
        )}

        {/* {this.state.usersList != null ? ( */}
        <React.Fragment>
          <div>
            {/* Render the list of items */}
            {/* {this.state.usersList.map((item, i) => {
                return <p key={i}>{item}</p>;
              })} */}
            {/* <p>{this.state.usersList}</p> */}
          </div>
          <Link to={"../"}>
            <button variant="raised">Go Back</button>
          </Link>
        </React.Fragment>
        {/* ) : ( */}
        {/* <div>
            <h2>No List Items Found</h2>
            <p>{this.state.gender}</p>
          </div> */}
        {/* )} */}
      </div>
    );
  }
}

export default Details;
