import React, { useEffect } from "react";
import { Pagination } from "react-bootstrap";
// import { Link } from "react-router-dom";

const Pages = ({ page, setPage }) => {
  useEffect(() => {
    //this functions sets the number of pages for the Pagination
    const setPages = () => {
      let items = [];
      for (let number = 0; number < 10; number++) {
        items.push(
          <Pagination.Item
            key={number + 1}
            active={number + 1 === page}
            onClick={() => setActivePage(number + 1)}
          >
            {number + 1}
          </Pagination.Item>
        );
      }
    };
    setPages();
  });

  //this function updates the page number state passed in
  const setActivePage = (pageNum) => {
    setPage(pageNum);
    console.log("active: " + page);
  };

  return (
    <div style={{ display: "flex", justifyContent: "center" }}>
      <Pagination>
        <Pagination.First />
        <Pagination.Prev />
        {items}
        <Pagination.Next />
        <Pagination.Last />
      </Pagination>
    </div>
  );
};

export default Pages;
