function formatDate(t) {
  let a = [{ day: "numeric" }, { month: "short" }, { year: "numeric" }];
  function format(m) {
    let f = new Intl.DateTimeFormat("en", m);
    return f.format(t);
  }
  return a.map(format).join(" ");
}

function greetUser(name) {
  return "Hello " + name.toString();
}

export default greetUser;
