import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { Spinner } from "react-bootstrap";

const Details = (props) => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    const { user } = props.location.state;
    setUserData(user);
    setLoading(false);
  }, []);

  // componentDidMount() {
  //   this.setState({ loading: true });
  //   const { user } = this.props.location.state;
  //   this.setState({ user_data: user, loading: false });
  //   console.log(user);
  // }

  // render() {
  function formatDate(t, a, s) {
    function format(m) {
      let f = new Intl.DateTimeFormat("en", m);
      return f.format(t);
    }
    return a.map(format).join(s);
  }

  let a = [{ day: "numeric" }, { month: "short" }, { year: "numeric" }];
  // let s = formatDate(new Date(), a, "-");

  return (
    <div className="App">
      {loading ? (
        <Spinner animation="border" variant="primary" />
      ) : (
        <div>
          <h1>{userData.name.first + " " + userData.name.last}</h1>
          <div>
            <img src={userData.picture.large} alt={"Profile pic"} />
          </div>
          <p>{userData.email}</p>
          <p>
            {userData.location.street.number +
              " " +
              userData.location.street.name +
              " " +
              userData.location.city +
              ", " +
              userData.location.state +
              " " +
              userData.location.postcode +
              " " +
              userData.location.country}
          </p>
          <p>{userData.phone}</p>
          <p>{formatDate(new Date(userData.dob.date), a, "/")}</p>
        </div>
      )}
      {/* Link to List.js */}
      <Link to={"../"}>
        <button variant="raised">Go Back</button>
      </Link>
    </div>
  );
};
// }
export default Details;
