import React, { useState, useEffect } from "react";
import MaterialTable from "material-table";
import { Link } from "react-router-dom";
import { Button } from "react-bootstrap";

// import { data } from "../data";

const MTable = ({ data }) => {
  const [selectedRow, setSelectedRow] = useState(null);

  useEffect(() => {
    const columns = [
      { title: "#", field: "number" },
      { title: "Last Name", field: "lastName" },
      { title: "First Name", field: "firstName" },
      { title: "Email", field: "email" },
      { title: "City", field: "city" },
      { title: "Country", field: "country" },
      { title: "Button", field: "button" },
    ];

    //convert data for table usage
    var temp = [];
    data.forEach((user, i) => {
      var obj = {};
      obj["number"] = i + 1;
      obj["lastName"] = user.name.last;
      obj["firstName"] = user.name.first;
      obj["email"] = user.email;
      obj["city"] = user.location.city;
      obj["country"] = user.location.country;
      obj["button"] = (
        <Link
          to={{
            pathname: "./Details",
            state: { user: user },
          }}
          className="text-info"
        >
          Visit Profile
        </Link>
      );

      temp.push(obj);
    });
  }, []);

  return (
    <div style={{ maxWidth: "80%", marginLeft: "auto", marginRight: "auto" }}>
      <MaterialTable
        columns={columns}
        data={temp}
        title="Users Directory"
        onRowClick={(evt, selectedRow) =>
          setSelectedRow(selectedRow.tableData.id)
        }
        options={{
          search: true,
          rowStyle: (rowData) => ({
            backgroundColor:
              selectedRow === rowData.tableData.id ? "#d9f7f7" : "#FFF",
          }),
        }}
        localization={{
          pagination: {
            labelDisplayedRows: "{from}-{to} of {count}",
          },
          toolbar: {
            nRowsSelected: "{0} row(s) selected",
          },
          header: {
            actions: "Actions",
          },
          body: {
            emptyDataSourceMessage: "No records to display",
            filterRow: {
              filterTooltip: "Filter",
            },
          },
        }}
      />
    </div>
  );
};
export default React.memo(MTable);
