import React, { Component, useState, useEffect } from "react";
import { Route, Switch } from "react-router-dom";
import "./App.css";
import Main from "./pages/Main";
import Details from "./pages/Details";

const App = () => {
  const [usersList, setUsersList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);
  const [seed, setSeed] = useState("abc");

  useEffect(() => {
    //function to call API from backend with page number as parameter
    //then order the users in the table and set state variables
    const getUsers = async () => {
      setLoading(true);
      var temp = [];
      await fetch("/api/getList/" + seed)
        .then((res) => res.json())
        // .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
        .then((list) => {
          temp = list;
          setSeed("def");
        });
      await fetch("/api/getList/" + seed)
        .then((res) => res.json())
        // .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
        .then((list) => {
          //combine both API call lists
          temp.push.apply(temp, list);
        });
      setUsersList(temp);
      setLoading(false);
    };
    getUsers();
  }, []);

  useEffect(() => {}, [seed]);

  const App = () => (
    <div>
      <Switch>
        <Route
          exact
          path="/"
          component={() => (
            <Main
              usersList={usersList}
              loading={loading}
              page={page}
              setPage={setPage}
            />
          )}
        />
        <Route path="/details" component={Details} />
      </Switch>
    </div>
  );
  return (
    <Switch>
      <App />
    </Switch>
  );
};

export default App;
