import React, { useEffect, useState } from "react";
import { Spinner } from "react-bootstrap";
import DataTable from "../components/DataTable";
import DataTable2 from "../components/DataTable2";

import Pages from "../components/Pages";

const Main = () => {
  //state variables using hooks
  const [usersList, setUsersList] = useState([]);
  const [loading, setLoading] = useState(false);
  const [page, setPage] = useState(1);

  //component mount and user retrieval
  useEffect(() => {
    //function to call API from backend with page number as parameter
    //then order the users in the table and set state variables
    const getUsers = async () => {
      setLoading(true);
      await fetch("/api/getList/" + page)
        .then((res) => res.json())
        // .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
        .then((list) => {
          setUsersList(list);
          setLoading(false);
        });
    };
    getUsers();
  }, [page]);

  return (
    <div className="App">
      <div style={{ margin: 50 }}>
        <h1>Clozd Assignment</h1>
        <h3>Mason Rich</h3>
      </div>
      {/* If loading show spinner, else show data table */}
      {loading ? (
        <Spinner animation="border" variant="primary" />
      ) : (
        // <DataTable data={usersList} />
        <DataTable2 data={usersList} page={page} />
      )}
      {/* <Pages setPage={setPage} page={page} /> */}
    </div>
  );
};

export default React.memo(Main);
