import React, { Component } from "react";
import Table from "react-bootstrap/Table";

class App extends Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>#</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Country</th>
          </tr>
        </thead>
        <tbody>
          {this.props.data.map((item, index) => (
            <tr>
              <td>{index + 1}</td>
              <td>{item.name.first + " " + item.name.last}</td>
              <td>{item.email}</td>
              <td>{item.location.city + ", " + item.location.country}</td>
              <Link
                to={{
                  pathname: "./Details",
                  state: { user: item },
                }}
              >
                <button variant="raised">View Details</button>
              </Link>
              <p>--------------------------------------------------</p>
            </tr>
          ))}
        </tbody>

        <tr>
          <td>1</td>
          <td>Mark</td>
          <td>Otto</td>
          <td>@mdo</td>
        </tr>
        <tr>
          <td>2</td>
          <td>Jacob</td>
          <td>Thornton</td>
          <td>@fat</td>
        </tr>
        <tr>
          <td>3</td>
          <td colSpan="2">Larry the Bird</td>
          <td>@twitter</td>
        </tr>
      </Table>
    );
  }
}

export default App;
