function formatDate(t) {
  let formatText = [
    { day: "numeric" },
    { month: "short" },
    { year: "numeric" },
  ];
  function format(m) {
    let f = new Intl.DateTimeFormat("en", m);
    return f.format(t);
  }
  return formatText.map(format).join("/");
}

