import React, { useState, useEffect, useHistory } from "react";
import { Link } from "react-router-dom";
import { Spinner } from "react-bootstrap";

const Details = () => {
  // state = {
  //   user_data: null,
  //   loading: true,
  // };
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const { user } = useHistory();

  //component mount and user retrieval
  useEffect(() => {
    //function to call API from backend with page number as parameter
    //then order the users in the table and set state variables
    const getUsers = async () => {
      setLoading(true);
      console.log(user);
      setUserData(user);
      setLoading(false);
    };
    getUsers();
  }, []);

  // componentDidMount() {
  //   this.setState({ loading: true });
  //   const { user } = this.props.location.state;
  //   this.setState({ user_data: user, loading: false });
  //   console.log(user);
  // }

  // render() {
  function formatDate(t, a, s) {
    function format(m) {
      let f = new Intl.DateTimeFormat("en", m);
      return f.format(t);
    }
    return a.map(format).join(s);
  }

  let a = [{ day: "numeric" }, { month: "short" }, { year: "numeric" }];
  // let s = formatDate(new Date(), a, "-");

  return (
    <div className="App">
      {this.state.loading ? (
        <Spinner animation="border" variant="primary" />
      ) : (
        <div>
          <h1>
            {this.state.user_data.name.first +
              " " +
              this.state.user_data.name.last}
          </h1>
          <div>
            <img src={this.state.user_data.picture.large} alt={"Profile pic"} />
          </div>
          <p>{this.state.user_data.email}</p>
          <p>
            {this.state.user_data.location.street.number +
              " " +
              this.state.user_data.location.street.name +
              " " +
              this.state.user_data.location.city +
              ", " +
              this.state.user_data.location.state +
              " " +
              this.state.user_data.location.postcode +
              " " +
              this.state.user_data.location.country}
          </p>
          <p>{this.state.user_data.phone}</p>
          <p>{formatDate(new Date(this.state.user_data.dob.date), a, "/")}</p>
        </div>
      )}
      {/* Link to List.js */}
      <Link to={"../"}>
        <button variant="raised">Go Back</button>
      </Link>
    </div>
  );
};
// }
export default Details;
