import React from "react";
import { Button, Card, Image } from "react-bootstrap";
import { Link } from "react-router-dom";
// import { uuid } from "uuidv4";

const UserCard = ({ data }) => {
  function formatDate(t, a, s) {
    function format(m) {
      let f = new Intl.DateTimeFormat("en", m);
      return f.format(t);
    }
    return a.map(format).join(s);
  }

  let format = [{ day: "numeric" }, { month: "short" }, { year: "numeric" }];

  return (
    <div className="d-flex justify-content-center pt-4">
      <Card bg={"light"} text={"dark"}>
        <Card.Header>
          <h2>{data.name.first + " " + data.name.last}</h2>
        </Card.Header>
        <Card.Body>
          <Image thumbnail src={data.picture.large} alt={"Profile pic"} />
          <Card.Text className="bg-success col-6 d-flex">Email</Card.Text>
          <Card.Text className="bg-success col-6 d-flex">
            {data.email}
          </Card.Text>

          <Card.Text className="bg-info">
            {data.location.street.number +
              " " +
              data.location.street.name +
              " \n" +
              data.location.city +
              ", " +
              data.location.state +
              " " +
              data.location.postcode +
              " " +
              data.location.country}
          </Card.Text>
          <Card.Text>
            <p>{data.phone}</p>
          </Card.Text>
          <Card.Text>
            <p>{formatDate(new Date(data.dob.date), format, "/")}</p>
          </Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
};

export default UserCard;
