import React, { Component } from "react";
import { Link } from "react-router-dom";
import { Button } from "react-bootstrap";
import Table from "../components/Table";

class Main extends Component {
  // Initialize the state
  constructor(props) {
    super(props);
    this.state = {
      usersList: [],
      loading: false,
    };
    this.logInfo = this.logInfo.bind(this);
  }

  // Fetch the list on first mount
  componentDidMount() {
    this.getList();
  }

  // Retrieves the list of items from the Express app
  getList = async () => {
    this.setState({ loading: true });
    console.log("List: ");
    await fetch("/api/getList")
      .then((res) => res.json())
      .then((list) => this.setState({ usersList: list, loading: false }));
  };

  logInfo() {
    console.log(this.state.usersList);
    console.log("gender: " + this.state.usersList[0].gender);
    var tempArr = [1, 2, 3, "yes", "no"];
    console.log(tempArr);
  }

  render() {
    return (
      <div className="App">
        <h1>List of Items</h1>
        <Button block variant="success" onClick={this.logInfo}>
          LOG
        </Button>
        {/* Check to see if any items are found*/}
        {this.state.loading ? (
          <p>LOADING</p>
        ) : (
          <React.Fragment>
            {this.state.usersList.map((item, index) => (
              <div>
                <p key={item.id.value}>
                  {item.name.first + " " + item.name.last}
                </p>
                <p key={item.id.value}>{item.email}</p>
                <p key={item.id.value}>
                  {item.location.city + ", " + item.location.country}
                </p>
                <Table name={item.name.first} />
                <p>--------------------------------------------------</p>
              </div>
            ))}
          </React.Fragment>
        )}

        <Link to={"./Details"}>
          <button variant="raised">View Details</button>
        </Link>
      </div>
    );
  }
}

export default Main;
