import React, { useEffect, useState } from "react";
import { Spinner } from "react-bootstrap";
import DataTable from "../components/DataTable";
import DataTable2 from "../components/DataTable2";

import Pages from "../components/Pages";

const Main = ({
  usersList,
  loading,
  offset,
  setOffset,
  tempData,
  setTempData,
}) => {
  //state variables using hooks
  // const [usersList, setUsersList] = useState([]);
  // const [loading, setLoading] = useState(false);
  // const [page, setPage] = useState(0);
  // const [seed, setSeed] = useState("abc");

  // useEffect(() => {
  //   console.log("useEffect called: " + page);
  // }, [page]);

  return (
    <div className="App">
      <div style={{ margin: 50 }}>
        <h1>Clozd Assignment</h1>
        <h3>Mason Rich</h3>
      </div>
      {/* If loading show spinner, else show data table */}
      {loading ? (
        <Spinner animation="border" variant="primary" />
      ) : (
        // <DataTable data={usersList} />
        // <DataTable2 data={usersList} page={page} setPage={setPage} />
        <DataTable2
          data={usersList}
          offset={offset}
          setOffset={setOffset}
          tempData={tempData}
          setTempData={setTempData}
        />
      )}
      {/* <Pages setPage={setPage} page={page} /> */}
    </div>
  );
};

export default React.memo(Main);
