import React, { useState, useEffect } from "react";
import ReactPaginate from "react-paginate";
import { Button, Table } from "react-bootstrap";
import { Link } from "react-router-dom";
import "../styles/DataTable.css";

/*

    NOTE: I got ideas of this Pagination code from https://blog.logrocket.com/4-ways-to-render-large-lists-in-react/   

*/
const DataTable2 = ({ data, offset, setOffset }) => {
  const [pagination, setPagination] = useState({
    data: data,
    offset1: 0,
    numberPerPage: 10,
    pageCount: 0,
    currentData: [],
  });

  useEffect(() => {
    setPagination((prevState) => ({
      ...prevState,
      pageCount: prevState.data.length / prevState.numberPerPage, //700
      currentData: prevState.data.slice(
        pagination.offset1, //10
        pagination.offset1 + pagination.numberPerPage //20
      ),
    }));
  }, [pagination.numberPerPage, pagination.offset1]);

  const handlePageClick = (event) => {
    const selected = event.selected;
    const offset1 = selected * pagination.numberPerPage; //10
    console.log("Offset in handle click: " + offset1);
    setPagination({ ...pagination, offset1 });
    // setOffset(offset1);
  };

  return (
    <div style={{ marginLeft: 100, marginRight: 100 }}>
      <Table striped bordered hover size="sm">
        <thead>
          <tr>
            <th>#</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Country</th>
            <th>Profile</th>
          </tr>
        </thead>
        <tbody>
          {pagination.currentData &&
            pagination.currentData.map((item, index) => (
              <tr key={index}>
                <td>{pagination.offset1 + (index + 1)}</td>
                <td>{item.name.last + ", " + item.name.first}</td>
                <td>{item.email}</td>
                <td>{item.location.city + ", " + item.location.country}</td>
                <td>
                  <Link
                    to={{
                      pathname: "./Details",
                      state: { user: item },
                    }}
                  >
                    <Button variant="link">Visit Profile</Button>
                  </Link>
                </td>
              </tr>
            ))}
        </tbody>
      </Table>

      <ReactPaginate
        previousLabel={"previous"}
        nextLabel={"next"}
        breakLabel={"..."}
        pageCount={pagination.pageCount}
        marginPagesDisplayed={2}
        pageRangeDisplayed={5}
        onPageChange={handlePageClick}
        containerClassName={"pagination"}
        activeClassName={"active"}
      />
    </div>
  );
};
export default DataTable2;
