import React, { Component } from "react";
import { Button, Pagination, Spinner } from "react-bootstrap";
import DataTable from "../components/DataTable";

class Main extends Component {
  // Initialize the state
  constructor(props) {
    super(props);
    this.state = {
      usersList: [],
      loading: false,
    };
  }

  // Fetch the list on first mount
  componentDidMount() {
    this.getList();
  }

  // Retrieves the list of items from the Node (Express) backend
  getList = async () => {
    this.setState({ loading: true });
    await fetch("/api/getList")
      .then((res) => res.json())
      .then((list) => list.sort((a, b) => (a.name.last > b.name.last ? 1 : -1)))
      .then((list) => this.setState({ usersList: list, loading: false }));
  };

  render() {
    return (
      <div className="App">
        <div style={{ margin: 50 }}>
          <h1>Clozd Assignment</h1>
          <h4>Mason Rich</h4>
        </div>
        {/* If loading show spinner, else show data table */}
        {this.state.loading ? (
          <Spinner animation="border" variant="primary" />
        ) : (
          <DataTable data={this.state.usersList} />
        )}
        <div style={{ display: "flex", justifyContent: "center" }}>
          <Pagination>
            <Pagination.First />
            <Pagination.Prev />
            <Pagination.Item>{1}</Pagination.Item>
            <Pagination.Ellipsis />

            <Pagination.Item>{10}</Pagination.Item>
            <Pagination.Item>{11}</Pagination.Item>
            <Pagination.Item active>{12}</Pagination.Item>
            <Pagination.Item>{13}</Pagination.Item>
            <Pagination.Item disabled>{14}</Pagination.Item>

            <Pagination.Ellipsis />
            <Pagination.Item>{20}</Pagination.Item>
            <Pagination.Next />
            <Pagination.Last />
          </Pagination>
        </div>
      </div>
    );
  }
}

export default Main;
