import React from "react";
import { Card, Image, ListGroup } from "react-bootstrap";
import * as util from "../functions/utilities";

const UserCard = ({ data }) => {
  // function formatDate(t, a, s) {
  //   function format(m) {
  //     let f = new Intl.DateTimeFormat("en", m);
  //     return f.format(t);
  //   }
  //   return a.map(format).join(s);
  // }

  // let format = [{ day: "numeric" }, { month: "short" }, { year: "numeric" }];

  return (
    <div className="d-flex justify-content-center pt-4">
      <Card bg={"light"} text={"dark"} style={{ width: "35rem" }}>
        <Card.Header>
          <h2>{data.name.first + " " + data.name.last}</h2>
        </Card.Header>
        <Card.Body>
          <Image thumbnail src={data.picture.large} alt={"Profile pic"} />
          <ListGroup variant="flush">
            <ListGroup.Item container className="bg-light">
              <p>{data.email}</p>
              <p>{"Phone: " + data.phone}</p>
              {/* <p>Born: {formatDate(new Date(data.dob.date), format, "/")}</p> */}
              <p>Born: {util.formatDate(new Date(data.dob.date))}</p>
            </ListGroup.Item>
            <ListGroup.Item className="bg-light">
              <p>
                {data.location.street.number + " " + data.location.street.name}
              </p>
              <p>
                {data.location.city +
                  ", " +
                  data.location.state +
                  " " +
                  data.location.postcode}
              </p>
              <p>{data.location.country}</p>
            </ListGroup.Item>
          </ListGroup>
        </Card.Body>
      </Card>
    </div>
  );
};

export default UserCard;
